import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';
import DefaultWait from './DefaultWait';
import { useState } from 'react';
import { HubConnectionBuilder, LogLevel } from '@microsoft/signalr';
import ChatRoom from './ChatRoom';

function App() {
  const[conn , setconnection]= useState();
  const[messages , setmessage]= useState([]);

  const JoinChatRoom = async (username, ChatRoom) => {
    try {
      const conn = new HubConnectionBuilder()
      .withUrl("https://localhost:7204/chat")
      .configureLogging(LogLevel.Information)
      .build();

      conn.onclose((error) => {
        console.error("Connection closed with error:", error);
      });

      conn.on("JoinSpecificRoom", (message) => {
        console.log("message : ", message);
      });

      conn.on("RecieveSpecificMessage", (username, message) => {
        setmessage( messages=>[...messages, {username, message}])
      });

    await  conn.start()
  .then(() => {
     console.log('Connection started');
  })
  .catch(error => {
     console.error('Error starting connection:', error);
  });

      await conn.invoke("JoinSpecificRoom", { username, ChatRoom });

      setconnection(conn);
    } catch(error){
      console.error('Error starting connection:', error);
    }


  }
  return (
    <div className="App">
      <main>
        <h2>AspChat</h2>
        <Container className="container">
          <Row>
            <Col>
              <h1>Bienvenu</h1>
            </Col>
          </Row>
          <Row className="align-items-center">
            <Col xs={12} md={6} className="form-container">
            { ! conn
              ? <DefaultWait JoinChatRoom={JoinChatRoom}/>
              : <ChatRoom messages={messages}></ChatRoom>
            }
            </Col>
          </Row>
        </Container>
      </main>
    </div>
  );

  }
export default App;
