import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';
import DefaultWait from './DefaultWait';
import { useState } from 'react';
import { HubConnectionBuilder, LogLevel } from '@microsoft/signalr';

function App() {
  const[connection, setconnection]= useState();

  const JoinChatRoom = async (username, ChatRoom) =>{
    try{
      const connection = new HubConnectionBuilder()
      .withUrl("https://localhost:7204/Chat")
      .configureLogging(LogLevel.Information)
      .build();

        connection.on("JoinSpecificChat", (username, message)=>{
          console.log("message : "+message); 
        }); 

        await connection.start(); 
        await connection.invoke("JoinSpecificChat", {username, ChatRoom});

        setconnection(connection); 
    }
    catch(e)
    {
      console.log(e); 
    }
  }
  return (
    <div className="App">
      <main>
        <h2>AspChat</h2>
        <Container className="container">
          <Row>
            <Col>
              <h1>Bienvenu</h1>
            </Col>
          </Row>
          <Row className="align-items-center">
            <Col xs={12} md={6} className="form-container">
              <DefaultWait JoinChatRoom={JoinChatRoom}/>
            </Col>
          </Row>
        </Container>
      </main>
    </div>
  );
}

export default App;
